    private void jMostrarInfoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMostrarInfoActionPerformed
        jDatos.append("Codigo: "+e.getCodigo()+"\n");
        jDatos.append("Nombre: "+e.getNombre()+"\n");
        jDatos.append("Seguro: "+e.getTipo_seg()+"\n");
        jDatos.append("Sueldo Base: "+e.getSueldoBase()+"\n");
        jDatos.append("Horas Extras: "+e.getHorasExtras()+"\n");
        jDatos.append("Area: "+e.getArea()+"\n");
        jDatos.append("Monto Extra: "+e.montoHExtras()+"\n");
        jDatos.append("Monto Seguro: "+e.montoSeguro()+"\n");
        jDatos.append("Monto EsSalud: "+e.montoEssalud()+"\n");
        jDatos.append("Monto de Descuentos: "+e.montoDescuentos()+"\n");
        jDatos.append("Sueldo Bruto: "+e.sueldoBruto()+"\n");
        jDatos.append("SueldoNeto: "+e.sueldoNeto()+"\n");
    }//GEN-LAST:event_jMostrarInfoActionPerformed

