    private void jCrearObjetoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCrearObjetoActionPerformed
        String cod      = jCodigo.getText();
        String nom      = jNombre.getText();
        String seguro   = jTipoSeguro.getSelectedItem().toString();
        double sueldo   = Double.parseDouble(jSueldoBase.getText());
        double h_extras = Double.parseDouble(jHExtras.getText());
        String area     = jArea.getSelectedItem().toString();
        
        e=new Empleado(cod,nom,area,sueldo,h_extras,seguro);
    }//GEN-LAST:event_jCrearObjetoActionPerformed

